const API_KEY = '36951c6cf443a4328c13b09d2b2941be';


const categorias = [

{
    name: "treding",
    title: "em alta",
    path: `/treding/all/week?api_key=${API_KEY}&language=pt-BR`

},

{
    name: "netflixOriginais",
    title: "Originais Netflix",
    path: `/discover/all/week?api_key=${API_KEY}&with_networks=213`

},

{
    name: "topRated",
    title: "Populares",
    path: `/movie/top/rated?api_key=${API_KEY}&language`

},

{
    name: "Comedy",
    title: "Comédias",
    path: `/discover/tv?api_key=${API_KEY}&with_genres=35`

},

{
    name: "romances",
    title: "Romances",
    path: `/discover/tv?api_key=${API_KEY}&with_genres=1074`

},

{
    name: "documentary",
    title: "Documentarios",
    path: `/discover/tv?api_key=${API_KEY}&with_genres=99`

}

];

export const getMovies = async (path) => {

    try {

        let url = `https://api.themoviedb.org/3${path}`;
        const response = await fetch(url);
        return await response.json();
        
    } catch (error) {
        console.log("error getMovies:error");
    }
}
 

export default categorias;
